/** @CustomParams
{
  "access_key": {
    "title": "Brave API Access Token",
    "key": "access_key",
    "description": "Your Brave Search API subscription token (x-subscription-token header)",
    "type": "string"
  },
  "query": {
    "title": "Query String",
    "key": "query",
    "description": "The search query you want to use",
    "type": "string"
  },
  "freshness": {
    "title": "Freshness",
    "key": "freshness",
    "description": "Freshness filter (pd=past day, pw=past week, pm=past month, py=past year)",
    "type": "string"
  }
}
*/
import axios from "axios";

export default async function run({ data }) {
  try {
    // Step 1: Perform search (node 7 functionality)
    const freshness = data.freshness === "null" || data.freshness == null ?  "pm" : data.freshness;
    const searchResponse = await axios({
      method: "GET",
      url: "https://api.search.brave.com/res/v1/web/search",
      headers: {
        "Accept": "application/json",
        "Accept-Encoding": "gzip",
        "x-subscription-token": data.access_key
      },
      params: {
        q: data.query,
        count: 5,
        safesearch: "strict",
        spellcheck: false,
        freshness: freshness,
        text_decorations: false,
        result_filter: "summarizer",
        summary: "true"
      }
    });

    // Extract summarizer key from search response
    const summarizerKey = searchResponse.data?.summarizer?.key;
    if (!summarizerKey) {
      return { 
        error: 'No summarizer key found in search response',
        search_response: searchResponse.data 
      };
    }

    // Step 2: Get summary using the summarizer key (node 8 functionality)
    const summaryResponse = await axios({
      method: "GET",
      url: `https://api.search.brave.com/res/v1/summarizer/summary?key=${encodeURIComponent(summarizerKey)}`,
      headers: {
        "Accept": "application/json",
        "Accept-Encoding": "gzip",
        "x-subscription-token": data.access_key
      }
    });

    // Step 3: Concatenate summary array (node 9 functionality)
    const summaryData = summaryResponse.data;
    let concatenated = '';

    if (summaryData?.summary && Array.isArray(summaryData.summary)) {
      concatenated = summaryData.summary
        .map(item => (item && typeof item.data === 'string') ? item.data : '')
        .join('');
    }

    return {
      search_response: searchResponse.data,
      summary_response: summaryData,
      concatenated: concatenated
    };

  } catch (error) {
    return { 
      error: `API request failed: ${error.message}`,
      details: error.response?.data || error.stack
    };
  }
}

