/** @CustomParams
{
	"database_id": {
		"type": "string",
		"title": "Database ID",
		"description": "The ID of the database to connect to"
	},
	"collection_name": {
		"type": "string",
		"title": "Collection Name",
		"description": "The name of the collection to search in"
	},
	"phone_number": {
		"type": "string",
		"title": "Phone Number"
	}
}
*/

export default async function run({ execution_id, input, data, store, db }) {
	const database_id = data.database_id;
	const collection_name = data.collection_name;

	const database = db.database(database_id);
	const collection = database.collection(collection_name);

	const filterObj = {
		conditions: [
			{
				operation: "equal",
				query: {
					path: "phone_number"
				},
				expected: {
					value: data.phone_number
				}
			}
		]
	};

	let existingUserObj;
	const objects = await collection.findObjects(10, 0, filterObj);
	console.log(`${objects.length} users found`);
	if (objects.length > 0) {existingUserObj = objects[0];}
	let newUser = {
		phone_number: data["{{$3.body.message.call.customer.number}}"]
	};
	if (data["{{$3.body.message.toolCalls.[0].function.arguments.first_name}}"] != "null") {newUser.first_name = data["{{$3.body.message.toolCalls.[0].function.arguments.first_name}}"];}
	if (data["{{$3.body.message.toolCalls.[0].function.arguments.last_name}}"] != "null") {newUser.last_name = data["{{$3.body.message.toolCalls.[0].function.arguments.last_name}}"];}

	if (objects.length === 0) {
		try {
			await collection.createObject(newUser);
			console.log(`Created user ${newUser.first_name} ${newUser.last_name} (${newUser.phone_number})`);
		} catch (error) {
			console.error(`Error creating user: ${error}`);
		}
	} else {
		// try {
		// 	await collection.updateObjects(filterObj, newUser);
		// 	console.log(`Updated user ${newUser.first_name} ${newUser.last_name} (${newUser.phone_number})`);
		// } catch (error) {
		// 	console.error(`Error updating object: ${error}`);
		// }
	}

	return {
		existingUserObj,
		newUser
	};
}

